"""Compress the string.

Use groupby from itertools.

groupby objects yield key-group pairs where the group is a generator.
Features:
* Group all consecutive items together.
* Group all occurrences of an item, given in a sorted iterable.
* Specify how to group items with a 'key function.'
"""
from itertools import groupby


def compress_the_string(st):
    """Compress the string."""
    output = ""
    li = [''.join(g) for k, g in groupby(st)]
    for i in li:
        output += str((len(i), int(i[0]))) + ' '
    return output


if __name__ == "__main__":
    # ST = "1222311"
    ST = ""
    print(compress_the_string(ST))
